use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;
use Symfony\Component\HttpFoundation\Request;
use Symfony\Component\HttpFoundation\Response;

class ErrorController extends AbstractController
{
    public function catchAll(Request $request): Response
    {
        $path = $request->getPathInfo();

        // Remove the trailing "/index.html" or "/" so we can do easier matching.
        if (str_ends_with($path, '/index.html')) {
            $path = substr($path, 0, -11);
        } elseif (str_ends_with($path, '/')) {
            $path = substr($path, 0, -1);
        }

        $rule = $this->pathRules($request)[$path] ?? null;

        if ($rule !== null) {
            if (isset($rule['template'])) {
                $response = $this->render($rule['template'], $rule['parameters'] ?? []);
            } else {
                $response = new Response();
            }

            if (isset($rule['status'])) {
                $response = $response->setStatusCode($rule['status']);
            }

            if (isset($rule['headers'])) {
                if ($rule['headers'] instanceof Closure) {
                    $headers = $rule['headers']();
                } else {
                    $headers = $rule['headers'];
                }
                $response->headers->add($headers);
            }
        } else {
            $response = new Response('<html><body>Whoops!</body></html>', 400);
        }

        return $response;
    }

