    private function doGone(): array
    {
        return [
            'status' => Response::HTTP_GONE,
            'template' => 'error/gone.html.twig',
        ];
    }

    private function doArchive(string $path): array
    {
        return $this->doRedirect(
            'https://web.archive.org/web/https://benramsey.com' . $path,
            Response::HTTP_TEMPORARY_REDIRECT,
        );
    }

    private function doRedirect(string $uri, int $status, Request $request = null): array
    {
        if ($request !== null && !str_starts_with($uri, 'http://') && !str_starts_with($uri, 'https://')) {
            $uri = $request->getUriForPath($uri);
        }

        return [
            'status' => $status,
            'headers' => [
                'location' => $uri,
            ],
            'template' => 'error/redirect.html.twig',
            'parameters' => [
                'status' => $status,
                'uri' => $uri,
            ],
        ];
    }
